body.script:
        const ENDPOINT = " YOUR API ENDPOINT HERE ";
        const alertSection = document.getElementById('alert-section');

        function requestFunction(data, method) {
            fetch(ENDPOINT, {
                method: method,
                headers: { 'Content-Type': 'application/json', },
                body: JSON.stringify(data),
            })
                .then((response) => response.json())
                .then((data) => {
                    window.location.href = data.url;
                    localStorage.setItem(data.id, data.key);
                })
                .catch((error) => {
                    alertSection.innerHTML = `
${error}`
                });
        }

        function createPaste() {
            const header = document.querySelector("#title").value;
            const content = document.querySelector("#code").value;
            const type = document.querySelector("#label").value;

            if (header.length > 0 && content.length > 0) {
                const data = { title: header, body: content, label: type };
                requestFunction(data, 'POST')
            } else {
                alertSection.innerHTML = `
Please provide a title and body.`
            }
        }

        function updatePaste() {
            const postId = document.querySelector("#hiddenSecretDiv").innerHTML;
            const formTitle = document.querySelector("#formTitle").value;
            const formBody = document.querySelector("#formCode").value;
            const formLabel = document.querySelector("#formLabel").value;
            const formKey = document.querySelector("#formEditCode").value;

            if (formTitle.length > 0 && formBody.length > 0) {
                if (formKey.length > 0) {
                    const formData = { id: postId, key: formKey, title: formTitle, body: formBody, label: formLabel }
                    requestFunction(formData, 'PUT')
                } else {
                    alertSection.innerHTML = `
Please provide edit code.`
                }
            } else {
                alertSection.innerHTML = `
Please provide a title and body.`
            }
        }

        function deletePaste() {
            const postId = document.querySelector("#hiddenSecretDiv").innerHTML;
            const formKey = document.querySelector("#formEditCode").value;
            localStorage.removeItem("postId");
            const formData = { id: postId, key: formKey }
            fetch(ENDPOINT, {
                method: 'DELETE',
                headers: { 'Content-Type': 'application/json', },
                body: JSON.stringify(formData),
            })
            window.location.href = '/'
        }
